mapper.update:
update comment set movie_id = #{movieId}, score= #{score}, comment = #{name}, user_id =#{userId},
time = #{time}, type = #{type}
where id =#{id}

mapper.select:
select comment.*, movie.name as movieName, user.name as userName from comment left join movie on comment.movie_id = movie.id left join user on comment.user_id = user.id
and movie.name like concat('%', #{movieName}, '%') and comment.user_id = #{userId}
order by comment.id desc
