mapper.select:
SELECT d.*, m.name AS movieName, u.name AS userName FROM discussion d LEFT JOIN movie m ON d.movie_id = m.id LEFT JOIN user u ON d.user_id = u.id
AND m.name LIKE concat('%', #{movieName}, '%')
AND d.user_id = #{userId}
AND d.movie_id = #{movieId}
AND d.title LIKE concat('%', #{title}, '%')
ORDER BY d.id DESC
